import React, { useState } from "react";

export default function App() {
  const [posts, setPosts] = useState([]);

  function getPosts() {
    const url = 'https://aspnetcorereacttutorial-aspnetserver.azurewebsites.net/get-all-posts';
    fetch(url, {
      method: 'Get'
    })
      .then(response => response.json())
      .then(postsFromServer => {
        console.log(postsFromServer);
        setPosts(postsFromServer);
      })
      .catch((error) => {
        console.log(error);
        alert(error);
      });
  }
  return (
    <div className="container">
      <div className="row min-vh-100">
        <div className="col d-flex flex-column justify-content-center align-items-center">
          <div>

            <h1>Asp .Net Core React Practice</h1>
            <div className="mt-5">
              <button onClick={getPosts} className="btn btn-dark btn-lg w-100">Get Posts from server</button>
            </div>
          </div>
          {posts.length > 0 && renderPostsTable()}
        </div>
      </div>
    </div>
  );

  function renderPostsTable() {
    return (
      <div className="table-responsive mt-5">
        <table className="table table-border border-dark">
          <thead>
            <tr>
              <th scope="col">PostId (Pk)</th>
              <th scope="col">Title</th>
              <th scope="col">Content</th>
              <th scope="col">CRUD  Operations</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => {
              <tr key={post.postId}>
                <th scope="row">{post.postId}</th>
                <td>{post.title}</td>
                <td>{post.content}</td>
                <td>
                  <button className="btn btn-dark btn-lg mx-3 my-3">Update</button>
                  <button className="btn btn-secondary btn-lg">Delete</button>
                </td>
              </tr>
            })}
          </tbody>
        </table>
        <button onClick={() => setPosts([])} className="btn btn-dark btn-lg w-100">Empty React post array</button>
      </div>
    )
  }
}
